$(document).ready(function() {
  $('.readmore').click(function(){
    $('#additionalText').slideDown();
    $('.readmore').hide();
    $('.readless').show();
    event.preventDefault();
  });
    $('.learnmore').click(function(){
    $('#learnmoretext').slideDown();
    $('.learnmore').hide();
    $('.learnmore').show();
    event.preventDefault();
  });
  $('.readless').click(function(){
    $('#additionalText').slideUp();
    $('.readless').hide();
    $('.readmore').show();
    event.preventDefault();
  });
});